<script>
import { defineComponent } from "vue";
import M from "materialize-css";

export default defineComponent({
  name: "Profile",
  components: {
  },
  methods:{
        SeViewToWector: function(time, resultHeight){
            document.querySelectorAll("#Vectors").forEach((elem)=> {
                var ViewHeight = elem.getAttribute("viewBox").split(" ")[1];
                var timeTic = time/100;
                var HeightTik = (resultHeight - ViewHeight)/(time/timeTic);
                var NumTic = 0;                        
                var animation = setInterval(() => {
                    var setHeight=ViewHeight/1+HeightTik*NumTic
                    if(NumTic>=time/timeTic){clearInterval(animation);
                        elem.setAttribute("viewBox","0 "+resultHeight+" 500 266");
                    }
                    elem.setAttribute("viewBox","0 "+setHeight+" 500 266");
                    NumTic++;
                },timeTic);  
                                          
            });
        },
        RewriteRegionName: function(name){
            document.getElementById("RegionName").innerHTML = name;
        },
        SelectRegion: function(id){
            let Region = document.getElementById(id);
            if( Region.classList.contains("SelectedRegion") ){
                Region.classList.remove("SelectedRegion");                
            }else{
                Region.classList.add("SelectedRegion");
            }            
        },
        AddTagToList: function(nameTag){            
            document.querySelectorAll("#SelectedTags").forEach((elem)=> {
                console.log(elem.innerHTML);
                var retInnerHtml = elem.innerHTML + nameTag;
                elem.innerHTML = retInnerHtml;
            });
        },
        SelectFolcler: function(id){
            let Folcler = document.getElementById(id);
            if( Folcler.classList.contains("SelectedFolcler") ){
                Folcler.classList.remove("SelectedFolcler");                
            }else{
                Folcler.classList.add("SelectedFolcler");
            } 
        },
        SelectGroup: function(id){
            let Group = document.getElementById(id);
            if( Group.classList.contains("ActiveGroupSelector") ){
                Group.classList.remove("ActiveGroupSelector");                
            }else{
                Group.classList.add("ActiveGroupSelector");
            }  
        }
    },
});
</script>
